window.JAVASCRIPT_MINIFIER_DATA={hero:{icon:"file-code-2",iconBg:"gradient-bg",title:"JavaScript Minifier",subtitle:"Minify and optimize your JavaScript code instantly with advanced options",features:[{icon:"zap",color:"text-yellow-500",label:"Instant Minification"},{icon:"settings",color:"text-blue-500",label:"Customizable Output"},{icon:"shield",color:"text-green-500",label:"Browser-based & Secure"}]},whatIs:{title:"What is JavaScript Minifier?",content:"The JavaScript Minifier is a free online tool that compresses and optimizes your JavaScript code by removing unnecessary whitespace, comments, and formatting. It helps reduce file size and improve website performance, while offering advanced options for output customization.<br><br><strong>Key features:</strong> Remove comments, keep or remove last semicolons, add line breaks after statements, and more. All processing is done in your browser for privacy and speed."},whyUse:{title:"Why Use JavaScript Minifier?",items:[{icon:"arrow-down",title:"Reduce File Size",desc:"Shrink your JavaScript files for faster load times and better performance."},{icon:"sliders",title:"Customizable Output",desc:"Choose to remove comments, keep last semicolons, and add line breaks as needed."},{icon:"lock",title:"Privacy First",desc:"All minification happens locally in your browser. Your JavaScript never leaves your device."},{icon:"copy",title:"Easy Copy & Export",desc:"One-click copy functionality for your minified JavaScript output."}]},howToUse:{title:"How to Use JavaScript Minifier",steps:[{icon:"file-input",title:"Input JavaScript",desc:"Paste your JavaScript code into the input area or use the sample JS buttons."},{icon:"settings",title:"Choose Options",desc:"Select your preferred minification options like removing comments or keeping last semicolons."},{icon:"minimize-2",title:"Minify",desc:"Click Minify to instantly compress your JavaScript. Copy the result with one click."}]},features:["Minify JavaScript with customizable options","Remove comments, whitespace, and unnecessary formatting","Option to keep or remove last semicolons","Add line breaks after statements for readability","Sample JavaScript for quick testing","One-click copy functionality","All processing done in your browser for privacy and security","Responsive design for all devices","No registration or installation required"],faq:[{q:"What does JavaScript minification do?",a:"JavaScript minification removes unnecessary characters like whitespace, comments, and line breaks to reduce file size and improve performance."},{q:"Will minification change how my JavaScript works?",a:"No, minification only removes characters that are not required for JavaScript to function. Your code logic will remain the same."},{q:"Is my JavaScript data secure?",a:"Yes, all processing is done locally in your browser. Your JavaScript is never sent to any server."},{q:"Can I keep comments or formatting?",a:"You can choose to keep or remove comments, last semicolons, and add line breaks after statements using the available options."},{q:"Why should I minify my JavaScript?",a:"Minified JavaScript loads faster, saves bandwidth, and improves website performance, especially for production environments."}]};