window.SPEECH_TO_TEXT_DATA={hero:{icon:"mic",iconBg:"gradient-bg",title:"Speech-to-Text",subtitle:"Convert your speech to text instantly in your browser, supporting multiple languages",features:[{icon:"mic",color:"text-blue-500",label:"Live Transcription"},{icon:"zap",color:"text-yellow-500",label:"Instant Results"},{icon:"globe",color:"text-green-500",label:"Browser-based"}]},whatIs:{title:"What is Speech-to-Text?",content:"Speech-to-Text is a free online tool that converts your spoken words into written text in real time. It uses your device's microphone and browser technology to transcribe speech instantly, supporting a wide range of languages.<br><br><strong>Easy to use:</strong> Just select your language, click the microphone, and start speaking. Copy or download your transcript with one click."},whyUse:{title:"Why Use Speech-to-Text?",items:[{icon:"mic",title:"Hands-Free Typing",desc:"Dictate notes, messages, or documents without touching your keyboard."},{icon:"zap",title:"Fast & Accurate",desc:"Get instant, real-time transcription as you speak."},{icon:"globe",title:"Multi-Language Support",desc:"Transcribe speech in many popular languages and dialects."},{icon:"copy",title:"Easy Export",desc:"Copy or download your transcript for use anywhere."}]},howToUse:{title:"How to Use Speech-to-Text",steps:[{icon:"mic",title:"Select Language",desc:"Choose your preferred language for transcription."},{icon:"wand-2",title:"Start Speaking",desc:"Click the microphone button and begin speaking clearly."},{icon:"copy",title:"Copy or Download",desc:"Copy your transcript to the clipboard or download as a text file."}]},features:["Live speech-to-text transcription in your browser","Supports multiple languages and dialects","No installation or signup required","Copy or download your transcript instantly","Responsive design for all devices","All processing is done locally for privacy and security"],faq:[{q:"Which browsers support this tool?",a:"Speech-to-Text works best in Chrome, Edge, and some other Chromium-based browsers. It may not work in Firefox or Safari."},{q:"Is my speech data sent to a server?",a:"No, all processing is done in your browser. Your speech is not sent to any server."},{q:"Can I use this tool on mobile?",a:"Yes, the tool is fully responsive and works on most modern mobile browsers that support speech recognition."},{q:"What languages are supported?",a:"The tool supports many popular languages and dialects. Select your language from the dropdown before starting."},{q:"Is there a time or length limit?",a:"There is no strict limit, but browsers may stop recognition after a few minutes for security reasons."}]};